import React, { useEffect, useState } from "react";
import axios from "axios";

const Buscador = () => {
  const [data, setData] = useState([]);

  const URL = "http://localhost:3011/getposts";

  const loadData = async () => {
    const response = await axios.get(URL);
    setData(response.data);
    console.log(response.data);
  };
 useEffect(() => {
    loadData();


 }, [])


  return (
    <div className="container-fluid">
      <div className="row">
        <div className="card cardhome">
          <div className="card-title">
            <h1>Todos los Posts</h1>

            <div className="form-group">
              <label htmlFor="buscarPor">Buscar por:</label>
        <div className="col-4">
            <label>Autor:</label>
            <input 
             className="form-control"
             type="text"

              />
        </div>
        <div className="col-4">
            <label>Titulo:</label>
            <input 
             className="form-control"
             type="text"

              />
        </div>
            </div>
          </div>



          <div className="row">
            {data.map((item, index) => (
              <div className="col-md-6" key={index}>
                <div className="cardposts">
                  <div className="card cardposts">
                    <div className="card-body">
                    <h3 className="card-title"> Autor:{item.st_Autor}</h3>
                      <h3 className="card-title"> {item.st_Titulo}</h3>
                      <div className="cardhome">{item.st_Contenido}</div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Buscador;
