import React, { useState } from 'react';
import CryptoJS from 'crypto-js';

const SignupForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [isValidEmail, setIsValidEmail] = useState(true);

  const handleEmailChange = (e) => {
    const inputEmail = e.target.value;
    setEmail(inputEmail);
    // Validacion del formato del correo electrónico
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    setIsValidEmail(emailRegex.test(inputEmail));
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handleSignup = () => {
    if (isValidEmail && username && password) {
      // Cifrar la contraseña en MD5
      const hashedPassword = CryptoJS.MD5(password).toString();
      // Aquí puedes enviar los datos del usuario (email, nombre de usuario y contraseña cifrada) al servidor
      // Puedes usar fetch o axios para enviar una solicitud POST al endpoint correspondiente.
      const userData = {
        email,
        username,
        password: hashedPassword,
      };
      console.log('Datos del usuario a enviar:', userData);

      // Aquí puedes realizar la solicitud POST al servidor
    } else {
      alert('Por favor, completa todos los campos y asegúrate de ingresar una dirección de correo electrónico válida.');
    }
  };

  return (
    <div className='container-fluid'>
      <div className='row'>
        <div className='col'>
          <div className='cardhome'>
            <h2 className='card-title'>Registro</h2>

            <label>Nombre de usuario:</label>
            <input className='input-form' type="text" value={username} onChange={handleUsernameChange} />


            <label>Correo Electrónico:</label>
            <input type="text" value={email} onChange={handleEmailChange} />
            {!isValidEmail && <p>Ingresa un correo electrónico válido.</p>}


            <label>Contraseña:</label>
            <input className='input-form' type="password" value={password} onChange={handlePasswordChange} />

            <button className='btn btnhomelog' onClick={handleSignup}>Registrarse</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignupForm;
