import React, { useState } from "react";
import { useAuth } from "./AuthUserContext";
import CryptoJS from "crypto-js";
import axios from "axios";

const Login = () => {
  const { login } = useAuth();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");


  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const hashedPassword = CryptoJS.MD5(password).toString();

    // Aquí puedes realizar la lógica de autenticación y llamar a la función de login
    const userData = {
      email,
      password: hashedPassword,
    };
    console.log(userData)
    try {

        const response = await axios.post("http://localhost:3011/login", userData);

        // Si la autenticación es exitosa, puedes llamar a la función de login del contexto
        login(response.data);

        // Limpia los campos después del envío (opcional)
        setEmail("");
        setPassword("");


      } catch (error) {
        // Maneja los errores, por ejemplo, muestra un mensaje de error al usuario
        console.error("Error al iniciar sesión:", error);
      }


    // Limpia los campos después del envío (opcional)
    setEmail("");
    setPassword("");
  };

  return (

<div className="container-fluid">
<div className="row cardhome">
  <div className="col">
    <h1>Login</h1>
    <h2>Iniciar Sesión</h2>
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="email">Correo Electrónico:</label>
        <input
          className="form-control"
          type="email"
          id="email"
          name="email"
          value={email}
          onChange={handleEmailChange}
          required
        />
      </div>
      <div>
        <label htmlFor="password">Contraseña:</label>
        <input
          className="form-control"
          type="password"
          id="password"
          name="password"
          value={password}
          onChange={handlePasswordChange}
          required
        />
      </div>
      <button className="btn btnhomelog" type="submit">
        Iniciar Sesión
      </button>
    </form>
  </div>
</div>
</div>  
  );
};

export default Login;
