import React, { useState } from "react";
import { MD5 } from "crypto-js";
import { useAuth } from "./AuthUserContext";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const hashedPassword = MD5(password).toString();

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Aquí puedes enviar la información de inicio de sesión al servidor o realizar la lógica de autenticación
    console.log("Email:", email);
    console.log("Contraseña:", hashedPassword);

    // Limpia los campos después del envío (opcional)
    setEmail("");
    setPassword("");
  };
  return (
    <div className="container-fluid">
      <div className="row cardhome">
        <div className="col">
          <h1>Login</h1>
          <h2>Iniciar Sesión</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="email">Correo Electrónico:</label>
              <input
                className="form-control"
                type="email"
                id="email"
                name="email"
                value={email}
                onChange={handleEmailChange}
                required
              />
            </div>
            <div>
              <label htmlFor="password">Contraseña:</label>
              <input
                className="form-control"
                type="password"
                id="password"
                name="password"
                value={password}
                onChange={handlePasswordChange}
                required
              />
            </div>
            <button className="btn btnhomelog" type="submit">
              Iniciar Sesión
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
