import React, { useEffect, useState } from "react";
import axios from "axios";




const Buscador = (props) => {
    const [FlagOffline,setFlagOffline]=useState(0)
    const [dataFromLocalStorage, setDataFromLocalStorage] = useState([]);





  const [data, setData] = useState([]);
  const [dataAut, setDataAut] = useState([]);
  const [busqueda, setBusqueda] = useState("");
  const [busquedat, setBusquedat] = useState("");
  const [busquedac, setBusquedac] = useState("");
  const [flag, setFlag] = useState(0);
  const [flagBuscador, setFlagBuscador] = useState("");


  const URL = "http://localhost:3011/getposts";
  const URLAUT = "http://localhost:3011/getpostsautor";
  const URLTI = "http://localhost:3011/getposttitle";
  const URLCONT = "http://localhost:3011/getpostpalabras";

  const loadData = async () => {
    const response = await axios.get(URL);
    setData(response.data);
    console.log(response.data);
    localStorage.setItem("datosEnLocalStorage", JSON.stringify(response.data));
    const localdatoss = JSON.parse(localStorage.getItem("datosEnLocalStorage"));
    console.log(localdatoss+"Datalocal")

  };

  const loadaut = async (busqueda) => {
    const searchdata = {
      busqueda: busqueda,
    };
    const response = await axios.post(URLAUT, searchdata);
    setDataAut(response.data);
    if (dataAut != null) {
      setFlag(1);
    }
    console.log(response.data);
  };

  const loadtitulo = async (busquedat) => {
    const searchdata = {
      busqueda: busquedat,
    };
    const response = await axios.post(URLTI, searchdata);
    setDataAut(response.data);
    if (dataAut != null) {
      setFlag(1);
    }
    console.log(response.data);
  };
  const loadcontenido = async (busquedac) => {
    const searchdata = {
      busqueda: busquedac,
    };
    const response = await axios.post(URLCONT, searchdata);
    setDataAut(response.data);
    if (dataAut != null) {
      setFlag(1);
    }
    console.log(response.data);
  };

  useEffect(() => {

    loadData();
    const handleOnlineStatus = () => {
        if (window.navigator.onLine) {
          setFlagOffline(2);
          alert("Usted entró en modo online");

        } else {
          setFlagOffline(1);
          alert("Usted entró en modo offline");
             const localStorageData = JSON.parse(localStorage.getItem("datosEnLocalStorage"));

    if (localStorageData) {
      setDataFromLocalStorage(localStorageData);
    }
        }
      };

      // Agrega un event listener para verificar el estado de conexión
      window.addEventListener("online", handleOnlineStatus);
      window.addEventListener("offline", handleOnlineStatus);




  }, []);

  const handleautor = (event) => {
    setBusqueda(event.target.value);
    setFlagBuscador("autor");
  };
  const handletitulo = (event) => {
    setBusquedat(event.target.value);
    setFlagBuscador("titulo");
  };

  const handlecontenido=(event)=>{
    setBusquedac(event.target.value);
    setFlagBuscador("contenido");
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (flagBuscador === "autor") {
      loadaut(busqueda);
    } else if (flagBuscador === "titulo") {
      loadtitulo(busquedat);
    }
    else if (flagBuscador === "contenido") {
        loadcontenido(busquedac);
      }

    console.log(busqueda);
  };

  return (
    <div className="container-fluid">

      <div className="row">
        <div className="card cardhome">

          <div className="card-title">
            <h1>Todos los Posts</h1>
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                <div className="row">
                  <div className="col-md-4">
                    <div className="form-group">
                      <label htmlFor="buscarPor">Buscar por Autor:</label>
                      <input
                        className="form-control"
                        type="text"
                        id="autorInput"
                        placeholder="Ingrese el autor"
                        value={busqueda}
                        onChange={handleautor}
                      />
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="form-group">
                      <label htmlFor="buscarPor">Buscar por Título:</label>
                      <input
                        className="form-control"
                        type="text"
                        id="tituloInput"
                        placeholder="Ingrese el título"
                        value={busquedat}
                        onChange={handletitulo}
                      />
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="form-group">
                      <label htmlFor="buscarPor">
                        Buscar por Palabra clave:
                      </label>
                      <input
                        className="form-control"
                        type="text"
                        id="palabraClaveInput"
                        placeholder="Ingrese una palabra clave"
                        value={busquedac}
                        onChange={handlecontenido}
                      />
                    </div>
                  </div>
                </div>
                <button className="btn btnhomelog">Buscar</button>
              </form>
            </div>
          </div>

          {FlagOffline === 1 ? (    <div>
            <p>Modo offline</p>

      {dataFromLocalStorage.map((item, index) => (
        <div className="card" key={index}>
          <div className="card-body">
            <h5 className="card-title">{item.title}</h5>
            <p className="card-text">{item.description}</p>
          </div>
        </div>
      ))}
    </div>):(<p>Modo online</p>) }


          {flag === 0 ? (
            <div className="row">
              {data.map((item, index) => (
                <div className="col-md-6" key={index}>
                  <div className="cardposts">
                    <div className="card cardposts">
                      <div className="card-body">
                        <h3 className="card-title"> Autor:{item.st_Autor}</h3>
                        <h3 className="card-title"> Titulo:{item.st_Titulo}</h3>
                        <div className="cardhome">{item.st_Contenido}</div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="row">
              {dataAut.map((item, index) => (
                <div className="col-md-6" key={index}>
                  <div className="cardposts">
                    <div className="card cardposts">
                      <div className="card-body">
                        <h3 className="card-title"> Autor:{item.st_Autor}</h3>
                        <h3 className="card-title"> Titulo:{item.st_Titulo}</h3>
                        <div className="cardhome">{item.st_Contenido}</div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Buscador;
