import React, { useEffect, useState } from "react";
import axios from "axios";
import { useAuth } from "../user/AuthUserContext";

const ReadPost = () => {
  const { userData } = useAuth();
  const idUser = userData.id_User;
  const autor=userData.st_NombreUser;

  const [data, setData] = useState([]);

  const URL = `http://localhost:3011/getpostsuser/${idUser}`;

  const loadData = async () => {
    const response = await axios.get(URL);
    setData(response.data);
    console.log(response.data);
  };
  useEffect(() => {
    loadData();
  }, []);

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col cardhome">
          <h1>Mis Posts</h1>
          <h2>Autor:{autor}</h2>

          <div className="row">
            {data.map((item) => {
              for (let i = 0; item; i++) {
                const titulo = item.st_Titulo;
                const contenido = item.st_Contenido;

                return (
                  <div className="cardposts">

                    <div className="col">
                      <h3 className="card-title">Titulo: {titulo}</h3>
                      <div className="cardhome">{contenido}</div>
                    </div>
                    <div className="card-body"></div>
                  </div>
                );
              }
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ReadPost;
