import React, { useState } from 'react';
import CryptoJS from 'crypto-js';

const SignupForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username,setUsername]=useState('');
  const [isValidEmail, setIsValidEmail] = useState(true);

  const handleEmailChange = (e) => {
    const inputEmail = e.target.value;
    setEmail(inputEmail);
    // Validacion delformato del correo electrónico
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    setIsValidEmail(emailRegex.test(inputEmail));
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleUserName=(e)=>{
    const inputUsername=e.target.value;
    setUsername(inputUsername);


  }

  const handleSignup = () => {
    if (isValidEmail) {
      // Cifrar la contraseña en MD5
      const hashedPassword = CryptoJS.MD5(password).toString();
      // Aquí puedes enviar los datos del usuario (email y contraseña cifrada) al servidor
      console.log('Email:', email);
      console.log('Password (MD5):', hashedPassword);
    } else {
      alert('Por favor, ingresa una dirección de correo electrónico válida.');
    }
  };

  return (
    <div className='container-fluid'>
      <div className='row'>
        <div className='col'>

      <div className='cardhome'>
      <h2 className='card-title'>Registro</h2>

        <label>Correo Electrónico:</label>
        <input type="text" value={email} onChange={handleEmailChange} />
        {!isValidEmail && <p>Ingresa un correo electrónico válido.</p>}


        <label>Nombre de usuario:</label>
        <input className='input-form' type="text" value={username} onChange={handlePasswordChange} />



        <label>Contraseña:</label>
        <input className='input-form' type="password" value={password} onChange={handlePasswordChange} />

      <button className='btn btnhomelog' onClick={handleSignup}>Registrarse</button>

      </div>
        </div>

      </div>

    </div>
  );
};

export default SignupForm;
