import React, { useState } from 'react';

const HomePage = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  const handleLoginClick = () => {
    setShowLogin(true);
    setShowRegister(false);
  };

  const handleRegisterClick = () => {
    setShowLogin(false);
    setShowRegister(true);
  };

  return (
    <div className="home-page">
      <div className="card">
        <h2>Bienvenido, soy Iv&aacute;n Acosta Suarez</h2>
        <p>Por favor, selecciona una opción:</p>
        <div className="buttons">
          <button onClick={handleLoginClick}>Iniciar Sesión</button>
          <button onClick={handleRegisterClick}>Registrarse</button>
        </div>
      </div>

      {showLogin && (
        <div className="login-form">
          {/* Aquí puedes colocar tu formulario de inicio de sesión */}
          <h3>Formulario de Inicio de Sesión</h3>
          {/* Agrega tus campos de usuario y contraseña aquí */}
        </div>
      )}

      {showRegister && (
        <div className="register-form">
          {/* Aquí puedes colocar tu formulario de registro */}
          <h3>Formulario de Registro</h3>
          {/* Agrega los campos necesarios para el registro aquí */}
        </div>
      )}
    </div>
  );
};

export default HomePage;
