import React, { useEffect, useState } from "react";
import axios from "axios";

const Buscador = () => {
  const [data, setData] = useState([]);
  const [dataAut,setDataAut]=useState([]);
  const [busqueda,setBusqueda]=useState("")
  const [flag,setFlag]=useState(0)
  const [flagBuscador,setFlagBuscador]=useState("")

  const URL = "http://localhost:3011/getposts";
  const URLAUT = "http://localhost:3011/getpostsautor";
  const URLTI = "http://localhost:3011/getposttitle";


  const loadData = async () => {
    const response = await axios.get(URL);
    setData(response.data);
    console.log(response.data);
  };

  const loadaut=async (busqueda) =>{

    const searchdata={
        busqueda:busqueda,
    }
    const response=await axios.post(URLAUT,searchdata);
    setDataAut(response.data);
    if(dataAut != null){
        setFlag(1)
    }
    console.log(response.data)
  }


  const loadtitulo=async (busqueda) =>{

    const searchdata={
        busqueda:busqueda,
    }
    const response=await axios.post(URLTI,searchdata);
    setDataAut(response.data);
    if(dataAut != null){
        setFlag(1)
    }
    console.log(response.data)
  }


 useEffect(() => {
    loadData();


 }, [])


 const handleautor=(event)=>{
    setBusqueda(event.target.value);
    setFlagBuscador("autor")
 }
 const handletitulo=(event)=>{
    setBusqueda(event.target.value);
    setFlagBuscador("titulo")
 }


 const handleSubmit=async(event)=>{
    event.preventDefault();
    console.log(flagBuscador)




  console.log(busqueda)


 }


  return (
    <div className="container-fluid">
      <div className="row">
        <div className="card cardhome">
        <div className="card-title">
      <h1>Todos los Posts</h1>
      <div className="card-body">
        <form onSubmit={handleSubmit}>
          <div className="row">
            <div className="col-md-4">
              <div className="form-group">
                <label htmlFor="buscarPor">Buscar por Autor:</label>
                <input
                  className="form-control"
                  type="text"
                  id="autorInput"
                  placeholder="Ingrese el autor"
                  value={busqueda}
                  onChange={handleautor}
                />
              </div>
            </div>
            <div className="col-md-4">
              <div className="form-group">
                <label htmlFor="buscarPor">Buscar por Título:</label>
                <input
                  className="form-control"
                  type="text"
                  id="tituloInput"
                  placeholder="Ingrese el título"
                  value={busqueda}
                  onChange={handletitulo}
                />
              </div>
            </div>
            <div className="col-md-4">
              <div className="form-group">
                <label htmlFor="buscarPor">Buscar por Palabra clave:</label>
                <input
                  className="form-control"
                  type="text"
                  id="palabraClaveInput"
                  placeholder="Ingrese una palabra clave"
                />
              </div>
            </div>
          </div>
          <button className="btn btnhomelog">Buscar</button>
        </form>
      </div>
    </div>



   {flag === 0 ?
    (
    <div className="row">
    {data.map((item, index) => (
      <div className="col-md-6" key={index}>
        <div className="cardposts">
          <div className="card cardposts">
            <div className="card-body">
            <h3 className="card-title"> Autor:{item.st_Autor}</h3>
              <h3 className="card-title"> Titulo:{item.st_Titulo}</h3>
              <div className="cardhome">{item.st_Contenido}</div>
            </div>
          </div>
        </div>
      </div>
    ))}
  </div>
  )
  :
  (
    <div className="row">
    {dataAut.map((item, index) => (
      <div className="col-md-6" key={index}>
        <div className="cardposts">
          <div className="card cardposts">
            <div className="card-body">
            <h3 className="card-title"> Autor:{item.st_Autor}</h3>
              <h3 className="card-title"> Titulo:{item.st_Titulo}</h3>
              <div className="cardhome">{item.st_Contenido}</div>
            </div>
          </div>
        </div>
      </div>
    ))}
  </div>

  )}


        </div>
      </div>
    </div>
  );
};

export default Buscador;
