import React, { useState } from "react";
import axios from "axios";


const Buscador = () => {


  const [data, setData] = useState([]);

  const URL = 'http://localhost:3011/getposts';

  const loadData = async () => {
    const response = await axios.get(URL);
    setData(response.data);
    console.log(response.data);
  };
  loadData();


  return (
    <div className="container-fluid">
      <div className="row">
        <div className="card cardhome">
          <div className="card-title">
            <h1>Todos los Posts</h1>

            <div className="form-group">
              <label htmlFor="buscarPor">Buscar por:</label>
              <select
                className="form-select form-select-lg mb-3" 
                aria-label="Large select example"
                id="buscarPor"

              >
                <option value="">Seleccione una opci√≥n</option>
                <option value="nombre">Titulo</option>
                <option value="nombre">Autor</option>
                <option value="nombre">Contenido del Post</option>


              </select>
            </div>
          </div>


<div className="row">
    <div className="col">


    </div>
</div>




<div className="row">
          {data.map((item, index) => (
            <div className="col-md-6" key={index}>
              <div className="cardposts">
                <div className="card cardposts">
                  <div className="card-body">
                    <h3 className="card-title"> {item.st_Titulo}</h3>
                    <div className="cardhome">{item.st_Contenido}</div>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
    </div>


    </div>
      </div>
  );
};

export default Buscador;
