import React, { useState } from 'react';
import { useAuth } from "../user/AuthUserContext";
const  Posts=()=> {


    const { userData } = useAuth();
  // Definir estados para el título y el mensaje
  const [titulo, setTitulo] = useState('');
  const [mensaje, setMensaje] = useState('');

  // Manejar cambios en el título
  const handleTituloChange = (event) => {
    setTitulo(event.target.value);
  };

  // Manejar cambios en el mensaje
  const handleMensajeChange = (event) => {
    setMensaje(event.target.value);
  };

  // Manejar el envío del formulario
  const handleSubmit = (event) => {
    event.preventDefault(); // Evitar que el formulario se envíe automáticamente
    // Aquí puedes realizar alguna acción con el título y el mensaje, como enviarlos a un servidor o realizar otra lógica
    console.log('Título:', titulo);
    console.log('Mensaje:', mensaje);
  };

  return (
    <div className='container-fluid'>
        <div className='row'>
            <div className='col cardhome'>
            <h1>Nuevo Post</h1>
            <h1>Bienvenido {userData.st_NombreUser}</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="titulo">Título:</label>
          <input
           className='form-control'
            type="text"
            id="titulo"
            value={titulo}
            onChange={handleTituloChange}
          />
        </div>
        <div>
          <label htmlFor="mensaje">Mensaje:</label>
          <textarea
           className='form-control'
           placeholder="Por user" 
            id="mensaje"
            value={mensaje}
            onChange={handleMensajeChange}
          ></textarea>
        </div>
        <button className='btn btnhomelog' type="submit">Enviar</button>
      </form>
            </div>
        </div>

    </div>
  );
}

export default Posts;
